import React from 'react';
import AppContext from './context';
import { Route } from 'react-router';

import './App.css';

import Header from './Components/Header';
import FeedBack from './Components/FeedBack';

function App() {

  const [menuOpened, setMenuOpened] = React.useState(false);
  const [aboutFeedOpen, setAboutFeedOpen] = React.useState(false);
  const [addressOpen, setAddressFeedOpen] = React.useState(false);
  const [infoOpen, setInfoFeedOpen] = React.useState(false);
  const [followOpen, setFollowFeedOpen] = React.useState(false);

  const onOpenAboutFeed = () => {
    setAboutFeedOpen(!aboutFeedOpen);
  }
  const onOpenAddressFeed = () => {
    setAddressFeedOpen(!addressOpen);
  }
  const onOpenInfoFeed = () => {
    setInfoFeedOpen(!infoOpen);
  }
  const onOpenFollowFeed = () => {
    setFollowFeedOpen(!followOpen);
  }

  const onOpenMenu = () => {
    setMenuOpened(!menuOpened);
  }

  return (
    <AppContext.Provider value={{menuOpened,
     onOpenMenu, aboutFeedOpen, onOpenAboutFeed,addressOpen,onOpenAddressFeed,infoOpen,onOpenInfoFeed
     }}>
      <Route path="/" exact>
      <div className="content">
      {menuOpened ? <div className="overlay"></div> : null}
      <Header />
      <div className="contentHome">
        <div className="homeCard">
          <h4>
          Essentials for the<br />
          changing season
          </h4>
          <div className="shopBlock">
            <p>Shop now</p>
          </div>
        </div>
      </div>
      <div className="blogBlock">
        <div className="blogContent c-1">
          <img className="blogImg" src="./images/i-3.jpg" alt="img" />
          <p className="blogTitle">Modular Packaging</p>
          <p className="blogTxt">Unboxing like never before</p>
        </div>
      </div>
      <div className="modelsBlock">
        <h4 className="modelsTxt">Our favorite models</h4>
        <div className="productBlock">
        <article className="product">
          <a href="http://localhost:3000/LT-01-White">
          <img className="productImg" src="./images/img1.jpg" alt="img" />
          </a>
          <p className="productTitle">LT 01 White</p>
          <p className="productPrice">€249</p>
        </article>
        <article className="product">
        <a href="http://localhost:3000/LT-01-Premium-Suede-Navy">
          <img className="productImg" src="./images/img2.jpg" alt="img" />
          </a>
          <p className="productTitle">LT 01 Premium Suede Navy</p>
          <p className="productPrice">€269</p>
        </article>
        <article className="product">        
        <a href="http://localhost:3000/LT-01-Court-Lite-White-/-Port">
          <img className="productImg" src="./images/img3.jpg" alt="img" />        
          </a>
          <p className="productTitle">LT 01 Court Lite White / Port</p>
          <p className="productPrice">€189</p>
        </article>
        <article className="product">
        <a href="http://localhost:3000/LT-01-ECONYL®-White-Close-Knit">
          <img className="productImg" src="./images/img4.jpg" alt="img" />
          </a>
          <p className="productTitle">LT 01 ECONYL® White Close-Knit</p>
          <p className="productPrice">€229</p>
        </article>
        </div>  
      </div>
      <div className="blogBlock">
        <div className="blogContent">
          <img className="blogImg" src="./images/i-1.jpg" alt="img" />
          <p className="blogTitle">ETQ Laundry Store</p>
          <p className="blogTxt">Let us clean your shoes</p>
        </div>
        <div className="blogContent">
          <img className="blogImg" src="./images/i-2.jpg" alt="img" />
          <p className="blogTitle">Amsterdam Flagship Store</p>
          <p className="blogTxt">Find us at Singel 465</p>
        </div>
      </div>
      <FeedBack />
    </div>
      </Route>
    <Route path="/LT-01-White" exact>
      <Header />
      <img className="productPageImg" src="./images/img1.jpg" alt="" />
      <img className="productPageImg" src="./images/p-2.png" alt="" />
      <img className="productPageImg" src="./images/p-3.jpg" alt="" />
      <img className="productPageImg" src="./images/p-4.jpg" alt="" />
      <img className="productPageImg" src="./images/p-5.jpg" alt="" />
    </Route>
    </AppContext.Provider>
  );
}

export default App;
