import React from 'react';
import axios from 'axios';

import ContentBottom from '../Components/ContentBottom';
import AppContext from '../context';

function LT01PremiumSuedeNavy() {

  const {setIsItemAdded, isItemAdded} = React.useContext(AppContext);

  const LT01PremiumSuedeNavy = [
    {
  "href": "http://localhost:3000/LT01PremiumSuedeNavy",
  "title": "LT 01 Premium Suede Navy",
  "price": 269,
  "color": "Black",
  "size": 35,
  "imgUrl": "./img/img2.jpg"
     }
  ]

  const onAddToBag = (obj) => {
    axios.post('https://6123b066124d8800175683c3.mockapi.io/bag', obj);
    setIsItemAdded(!isItemAdded);
  }

  return (
    <>
    <div className="product">
      <img src="./img/img2.jpg" alt="img" />
      <img src="./img/img2-2.png" alt="img" />
      <img src="./img/img2-3.jpg" alt="img" />
      <img src="./img/img2-4.jpg" alt="img" />
      <img src="./img/img2-5.jpg" alt="img" />
    </div>
    {
      LT01PremiumSuedeNavy.map((obj, index) => (
        <ContentBottom
         key={index}
         title={obj.title}
         size={obj.size}
         price={obj.price}
         imgUrl={obj.imgUrl}
         color={obj.color}
         onPlus={onAddToBag} />
      ))
    }
    </>
  );
}

export default LT01PremiumSuedeNavy;
