import React from 'react';
import Card from '../Components/Card';
import AppContext from '../context';

function Home() {

  const {onChangeInput, items, searchValue} = React.useContext(AppContext)

  return (
    <div className="container">
        <h4 className="h4Text">Our favorite models</h4>
        <div className="searchBlock align-c">
          <input onChange={onChangeInput} placeholder="Start typing what you're looking for" type="text" className="search" />
          <img width="20" src="./img/search.svg" alt="search" className="searchSvg" />
        </div>
      <div className="products">
        {
          items.filter(item => item.title.toLowerCase().includes(searchValue.toLocaleLowerCase())).map((item, index) => (
          <Card
          key={index}
          href={item.href}
          title={item.title}
          img={item.imgUrl}
          price={item.price} />
        ))
        }
      </div>
      </div>
  );
}

export default Home;
