import React from 'react';
import axios from 'axios';

import ContentBottom from '../Components/ContentBottom';
import AppContext from '../context';

function LT01White() {

  const {setIsItemAdded, isItemAdded} = React.useContext(AppContext);

  const LT01White = [
    {
      "title": "LT 01 White",
      "price": 249,
      "color": "White",
      "size": 35,
      "imgUrl": "./img/img1.jpg"
     }
  ]

  const onAddToBag = (obj) => {
    axios.post('https://6123b066124d8800175683c3.mockapi.io/bag', obj);
    setIsItemAdded(!isItemAdded);
  }

  return (
    <AppContext.Provider value={{isItemAdded}}>
    <>
    <div className="product">
      <img src="./img/img1.jpg" alt="img" />
      <img src="./img/img1-2.png" alt="img" />
      <img src="./img/img1-3.jpg" alt="img" />
      <img src="./img/img1-4.jpg" alt="img" />
      <img src="./img/img1-5.jpg" alt="img" />
    </div>
    {
      LT01White.map((obj, index) => (
        <ContentBottom
         key={index}
         title={obj.title}
         size={obj.size}
         price={obj.price}
         imgUrl={obj.imgUrl}
         color={obj.color}
         onPlus={onAddToBag} />
      ))
    }
    </>
    </AppContext.Provider>
  );
}

export default LT01White;
