import React from 'react';
import axios from 'axios';
import { Link } from "react-router-dom";

function Header() {

  const [bagItems, setBagItems] = React.useState([]);

  React.useEffect(() => {
    async function rendBagItems() {
      const bagItemsResponse = await axios.get('https://6123b066124d8800175683c3.mockapi.io/bag');
      setBagItems(bagItemsResponse.data);
    };

    rendBagItems();
  }, []);

  return (
    <header className="contentHeader align-c">
      <div className="menuBlock">
        <img width="20" src="./img/menu.svg" alt="menu" />
      </div>
    <div className="headerLeft c-p align-c">
      <Link to="/">
      <img width="75" src="./img/logo.png" alt="logo"></img>
      </Link>
    </div>
        <div className="headerRight align-c">
      <div className="cartBlock">
      <Link to="/bag">
      <img width="20" src="./img/cart.svg" alt="cart" className="cartSvg c-p" />
      </Link>
      {
      bagItems.lenght < 0 ? <span className="activatedCart"></span> :null
      }
      </div>
    </div>
  </header>
  );
}

export default Header;
