import React from 'react';
import axios from 'axios';

import './App.css';

import Header from './Components/Header';
import Chat from './Components/Chat';
import Card from './Components/Card';

function App() {

  const [items, setItems] = React.useState([]);
  const [searchValue, setSearchValue] = React.useState([]);

  React.useEffect(() => {
    async function fetchData() {
      const itemsResponse = await axios.get('https://6123b066124d8800175683c3.mockapi.io/items');
      setItems(itemsResponse.data);
    };
    fetchData();
  }, []);

  const onChangeInput = (event) => {
    setSearchValue(event.target.value);
  }

  return (
      <div className="content">
      <Chat />
      <Header />
      <div className="container">
        <h4 className="h4Text">Our favorite models</h4>
        <div className="searchBlock align-c">
          <input onChange={onChangeInput} placeholder="Start typing what you're looking for" type="text" className="search" />
          <img width="20" src="./img/search.svg" alt="search" className="searchSvg" />
        </div>
      <div className="products">
        {
          items.filter(item => item.title.includes(searchValue)).map((item, index) => (
            <Card
            key={index}
            href={item.href}
            title={item.title}
            img={item.imgUrl}
            price={item.price} />
          ))
        }
      </div>
      </div>
    </div>
  );
}

export default App;
