import React from 'react';
import AppContext from '../context';

function ContentBottom({title, price, size, imgUrl, index, color, onPlus, id}) {
 
  const onClickPlus = () => {
    onPlus({title, size, price, imgUrl, color, id});
  }

  const {isItemAdded} = React.useContext(AppContext);

  return (
    <div key={index} className="contentBottom">
      <img className="hiddenImg" src={imgUrl} alt="img"></img>
    <p className="itemTitle">{title} - â‚¬{price}</p>
    <div className="contentBottomBottom align-c">
    <div className="chooseSize align-c">
      <p>Size - {size}</p>
     <img width="8" src="./img/caret-down.png" alt="caret" />
    </div>
    {
      isItemAdded ? <div onClick={onClickPlus} className="addedToBag align-c">
      <p>Added to bag!</p>
     </div> :
      <a href="">
     <div onClick={onClickPlus} className="addToBag align-c">
     <p>Add to bag</p>
    </div>
    </a>
    }
    </div>
  </div>
  );
}

export default ContentBottom;
