import axios from 'axios';
import React from 'react';
import { Link } from "react-router-dom";

import BagCard from "../Components/BagCard";

function Bag() {

  // const {bagItems} = React.useContext(AppContext);

  const [bagItems, setBagItems] = React.useState([]);

  React.useEffect(() => {
    async function rendBagItems() {
      const bagItemsResponse = await axios.get('https://6123b066124d8800175683c3.mockapi.io/bag');
      setBagItems(bagItemsResponse.data);
    };

    rendBagItems();
  }, []);

  return (
    <>
    <div className="bag">
      <Link to="/">
      <div className="bagBlock align-c">
        <img alt="arrow-left" width="20" src="./img/arrow-left.svg"></img>
        <p>Keep shopping</p>
      </div>
      </Link>
      <div className="bagItemsBlock">
        <h4 className="Bagtxt">Bag</h4>
        <hr />
        {
          bagItems.map((item, index) =>(
            <BagCard
             key={index}
             imgUrl={item.imgUrl}
             title={item.title}
             price={item.price}
             color={item.color}
             size={item.size} />
          ))
        }
        <hr />
      </div>
    </div>
          <div className="bagContentBottom">
          <div className="totalPrice align-c">
            <p className="totalTxt">Total</p>
            <p className="itemPrice totalIBagPrice">â‚¬498</p>
          </div>
          <div className="cheakoutBtn align-c">
            <p>Chekout</p>
          </div>
          </div>
          </>
  );
}

export default Bag;
